                                    l.o_zip, l.d_zip, l.o_city, l.d_city, l.o_haul, l.o_loading, l.o_loadsize, l.o_stairs,                                         l.o_traileravlble, l.d_delivary, l.d_comments, NULL AS space_available, NULL AS trailer_size,                                        NULL AS comments                                    FROM                                         wp_loads AS l                                    INNER JOIN                                         wp_user_postaccount AS p                                     ON                                         l.purchased_package_id = p.id                                     WHERE                                         l.userid = $user_id                                    AND                                                l.post_expiry_date >= CURDATE()                                     AND                                         p.expiry_date >= CURDATE()                                    UNION                                                                          SELECT                                         s.id_space AS id, s.post_type, s.post_date, s.userid, s.o_state, s.d_state, s.o_date, s.d_date,                                         NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, s.space_available, s.trailer_size,                                         s.comments                                    FROM                                         wp_spaces AS s                                    INNER JOIN                                         wp_user_postaccount AS p                                     ON                                         s.purchased_package_id = p.id                                     WHERE                                        s.userid = $user_id                                    AND                                        s.post_expiry_date >= CURDATE()                                     AND                                         p.expiry_date >= CURDATE()                                    ORDER BY                                        post_date                                    DESC ";                                    $myrows_wp_loads = $wpdb->get_results($query);                                    $page_url = currentPageURL();                                    $get_page_no = end(explode('/', $page_url));                                    if(is_numeric($get_page_no[0])){                                        $page_no = $get_page_no[0];                                    }else{                                        $page_no = 1;                                    }                                    if( !empty($myrows_wp_loads) ) {                                        echo getPaginationString($page_no, $wpdb->num_rows, 5, 1, site_url().'/view-open-post/', "");                                     }else{ ?>                                        <div class="not_found"><p>Currently there is no post.</p></div>			                                    <?php                                     }                                ?>                           </div>                        </div>                                                                </div>                </div>            </div>            <!--cnt_div-end-->    </div><?php get_footer(); ?>